import React, { Component } from 'react';
import '../../styles/LoginForm.css';

class LoginForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            username: "",
            password: ""
        }
    }

    ChangeUserName(event){
        this.setState({username: event.target.value});
    }

    ChangePassword(event){
        this.setState({password: event.target.value});
    }

    render() {
        return (
            <div>
                <div class="sidenav">
                    <div class="login-main-text">
                        <h2>RealTimeChat<br /> Login Page</h2>
                        <p>Login or register from here to access.</p>
                    </div>
                </div>
                <div class="main">
                    <div class="col-md-6 col-sm-12">
                        <div class="login-form">
                            <form>
                                <div class="form-group">
                                    <label>User Name</label>
                                    <input type="text" class="form-control" 
                                        onChange={this.ChangeUserName.bind(this)} placeholder="User Name" />
                                </div>
                                <div class="form-group">
                                    <label>Password</label>
                                    <input type="password" class="form-control" 
                                        onChange={this.ChangePassword.bind(this)} placeholder="Password" />
                                </div>
                                <button type="submit" class="btn btn-black" 
                                    onClick={this.props.login(this.state.username, this.state.password)}>Login</button>
                                <button type="submit" class="btn btn-secondary">Register</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default LoginForm;
