import React, { Component } from 'react';
import './styles/App.css';
import ChatContainer from './components/ChatContainer/ChatContainer';
import LoginForm from './components/LoginForm/LoginForm'
import * as websocket from 'websocket'
import * as Config from './Config'

const client = new websocket.w3cwebsocket('ws://127.0.0.1:8082');

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLogin: false,
      user: null
    }
  }

  UNSAFE_componentWillMount(){
    client.onopen = () => {
      console.log('WebSocket Client Connected');
    };
    client.onmessage = (message) => {
      console.log(message.data);
      if(message.data === Config.LOGIN_CODE_SUCCESS){
        this.setState({
          isLogin: true
        })
      }
    };
    client.onclose = () => {
      console.log("connection closed");
    }
  }

  Login = (event, username, password) => {
    client.send(JSON.stringify({
      code: Config.LOGIN_CODE,
      username: username,
      password: password
    }));

    event.preventDefault();
  }

  render() {
    return this.state.isLogin ? <ChatContainer></ChatContainer> : <LoginForm login={this.Login}></LoginForm>;
  }
}

export default App;
